import React, { useRef, useEffect } from 'react'
import {map1, plant1, plants} from './InitialFile.js'

const Canvas = props => {
    const canvasRef = useRef(null)    
    const draw = ctx => {
      for(let i = 10; i < ctx.canvas.width; i+=10)
      {
        drawLine(ctx, i, 0, i, ctx.canvas.height, "black");
      }
      for(let i = 10; i < ctx.canvas.height; i+=10)
      {
        drawLine(ctx, 0, i, ctx.canvas.width, i, "#000000");
      }

      //let temp = new plant();
      
      for (let i = 0; i < map1.num_initial_plants; i++)
      {
        temp = plant1;
      drawCircle(ctx, plant1.x, plant1.y, "green");
      }
    }

    function drawLine(ctx, x1, y1, x2, y2, bColor){
      ctx.beginPath();
      ctx.moveTo(x1, y1);
      ctx.lineTo(x2, y2);
      ctx.lineWidth = .5;
      ctx.strokeStyle = bColor;
      ctx.fillStyle = bColor;
      ctx.fill();
      ctx.stroke();
    }

    function drawCircle(ctx, x1, y1, bColor){
      ctx.beginPath();
      ctx.arc(x1+5, y1+5, 5, 0, 2*Math.PI);
      ctx.lineWidth = 1;
      ctx.strokeStyle = bColor;
      ctx.fillStyle = bColor;
      ctx.fill();
      ctx.stroke();
    };

    useEffect(() => {
      const canvas = canvasRef.current
      const context = canvas.getContext('2d')
      context.canvas.width = map1.width;
      context.canvas.height = map1.height;
      context.canvas.style = "border:1px solid #000000;";
      //Our draw come here
      draw(context)
    }, [draw])
    
    return <canvas ref={canvasRef} {...props}/>
  
  }

  export default Canvas;
