import React, {createRef} from "react";
import './App.css';
import {map1} from './InitialFile';
//let file 

//https://youtu.be/73SpYrjsNlk?si=ziNN44rcPouppVKA
function App() {
  const fileInput = createRef();

   const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.set("avatar", fileInput.current.files[0]);

    try{
        const response = await fetch('/profile',{
            method: "POST",
            body: formData
        });

        await response.json();
        if (response.ok) {
            alert("File uploaded");
        } else {
            console.error("some error occured");
        }
    }catch (e) {
        console.error(e.message)
    }
}

   return (
   <div>
      <h2 id="demo">Upload Initial File</h2>
           <form onSubmit={onSubmit}>
                <input type="file" name="avatar" ref={fileInput}/>
                <input type="submit" value="Submit"/>
                <h1>{map1.num_plants}</h1>
           </form>
   </div>


   );
}
export default App;
